mapper.update:
update tbl_permission
set expression = #{expression,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, expression, name,parentID
from tbl_permission
where id = #{id,jdbcType=BIGINT}

select id, expression, name,parentID
from tbl_permission
where name = #{name}

select id, expression, name,parentID
from tbl_permission
where expression = #{expression}
