mapper.update:
update tbl_role
set sn = #{sn,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, sn, name
from tbl_role
where id = #{id,jdbcType=BIGINT}

select id, sn, name
from tbl_role
where sn = #{sn}

select id, sn, name
from tbl_role
where name = #{name}
